const e=document.querySelector("body"),o=e.querySelector(".mode-toggle"),t=e.querySelector("nav"),a=e.querySelector(".sidebar-toggle");let s=localStorage.getItem("mode");s&&s==="dark"&&e.classList.toggle("dark");let l=localStorage.getItem("status");l&&l==="close"&&t.classList.toggle("close");o.addEventListener("click",()=>{e.classList.toggle("dark"),e.classList.contains("dark")?localStorage.setItem("mode","dark"):localStorage.setItem("mode","light")});a.addEventListener("click",()=>{t.classList.toggle("close"),t.classList.contains("close")?localStorage.setItem("status","close"):localStorage.setItem("status","open")});
